row.process:
sql_statement=$(cat <<-END
select 'Connection Percent',
  CAST(a.current_connection_count AS DOUBLE PRECISION)/CAST(b.max_connections AS DOUBLE PRECISION) * 100
from (
  SELECT count(*) current_connection_count
  from pg_stat_activity) a, (
  SELECT name, setting max_connections
  FROM pg_settings
  WHERE name = 'max_connections'
) b
END
)

echo "SQL Statement:${sql_statement}"
echo 'psql -c "<sql_statement>" postgres -A -t -h &GREENPLUM_SERVER#;'

psql -c "${sql_statement}" postgres -A -t -h &GREENPLUM_SERVER#;
