row.process:
:INCLUDE PCK.ITPA_SHARED.PRV.INCLUDE.CHECK_POWERSHELL_CMD@WINDOWS

:SET &UC4RB_CONVERTED_STRING# = "&UC4RB_ARCHIVE_PATH#\&UC4RB_OUT_TARGET_GUID#\snap.xml"
:INCLUDE PCK.ITPA_SHARED.PRV.INCLUDE.CONVERT_SLASHES_WINDOWS_STYLE
:SET &SNAPSHOT_FILE# = &UC4RB_CONVERTED_STRING#

Select-Xml -path "&SNAPSHOT_FILE#" -xpath "/snapshot" | foreach {
 Write-Host Websphere: $_.Node.host :: $_.Node.port
}
:INCLUDE PCK.ITPA_SHARED.PRV.INCLUDE.CHECK_POWERSHELL_CMD@WINDOWS

$application = (Select-Xml -path "&SNAPSHOT_FILE#" -xpath /snapshot/application).Node.type

$server = (Select-Xml -path "&SNAPSHOT_FILE#" -xpath /snapshot/server).Node.type

if($application -and $server){
  Write-Host "SnapMode: BOTH"
}elseif($application -and -not $server){
  Write-Host "SnapMode: APPLICATION"
}elseif($server -and -not $application){
  Write-Host "SnapMode: SERVER"
}else {
  Write-Host "Unable to extract information from &SNAPSHOT_FILE#"
  exit 1
}
Select-Xml -path "&SNAPSHOT_FILE#" -xpath "/snapshot/application/appname" | foreach {
 Write-Host AppName: $_.Node.name
}
